function createBookCard(book) {
  const card = document.createElement("div");
  card.classList.add("card");

  const img = document.createElement("img");
  img.src = book.img;
  img.alt = book.title;
  img.width = 170;
  img.height = 220;

  const title = document.createElement("h2");
  title.textContent = book.title;

  const author = document.createElement("p");
  author.textContent = book.author;

  card.appendChild(img);
  card.appendChild(title);
  card.appendChild(author);

  return card;
}

function displayBooks(books, containerElement) {
  containerElement.innerHTML = "";

  if (books.length > 0) {
    books.forEach((book) => {
      const card = createBookCard(book);
      containerElement.appendChild(card);
    });
  } else {
    containerElement.textContent = "Sorry, we do NOT have that item.";
  }
}

function handleSearch() {
  const searchText = document
    .getElementById("search-input")
    .value.trim()
    .toLowerCase();

  const filteredBooks = books.filter((book) =>
    book.name.toLowerCase().includes(searchText)
  );

  const booksContainer = document.querySelector(".books-container");
  displayBooks(filteredBooks, booksContainer);
}

const searchButton = document.getElementById("search-button");
searchButton.addEventListener("click", handleSearch);

const searchInput = document.getElementById("search-input");
searchInput.addEventListener("keypress", function (event) {
  if (event.key === "Enter") {
    handleSearch();
  }
});

displayBooks(books, document.querySelector(".books-container"));
